import { Component } from 'react';
import axios from 'axios';
import ResultsDisplay from '../resultsDisplay/ResultsDisplay';
import { useState } from 'react';

function SearchBar () {
    const [query, setQuery] = useState("");
    const searchImages = async (e) => {
        e.preventDefault();
        console.log("Submitting the Form")
        var API_KEY = '32265864-051f1f4a77558a7656ff80c35';
        axios.get(`https://cors-anywhere.herokuapp.com/https://pixabay.com/api/?key=${API_KEY} &q=${this.state.searchText}
                &safesearch=true`)
    };

    console.log(query);
    return (
        <form className="form" onSubmit={searchImages}>
            <div style={{padding: '50px'}}>
                <input type="text" name="searchText" 
                    className="input" 
                    value={query} 
                    onChange={(e) => setQuery(e.target.value)}/>

                <button type="submit" > Search </button>
            </div>
        </form>

    )



}



async function displayImages () {
    var images= await searchImages();
